def check_missing(data):
    missing_flag = False
    for column in data.columns:
        missing_count = data[column].isnull().sum()
        if missing_count > 0:
            print(f"Feature '{column}' has {missing_count} missing values.")
            missing_flag = True

    if not missing_flag:
        print("No missing data in the whole dataset.")

